(42, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'Renewal_notification_emails_Woosubscriptions_Nov2024', 'marketing', 'en_US', 'WooCommerce Subscriptions just got even better', 'You can now send automatic email reminders to customers before their subscriptions renew or expire. To enable this for your store, update to the latest version of WooCommerce Subscriptions (don\'t forget to make a backup first) and follow the steps linked below.', '{}', 'pending', 'woocommerce.com', '2024-11-19 18:07:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Bir tema mı seçiyorsunuz?', 'WooCommerce ile uyumlu temalara göz atın ve markanız ile iş ihtiyaçlarınıza uygun olanı seçin.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-20 18:08:50', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'wc-admin-launch-checklist', 'info', 'en_US', 'Mağazanızı yayınlamaya hazır mısınız?', 'Moral bozan \"acaba neyi unuttum?\" hissine kapılmamak için, lansman öncesi temel kontrol listesini oluşturduk.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-21 18:45:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-mobile-app', 'info', 'en_US', 'Woo mobil uygulamasını yükle', 'Nerede olursanız olun siparişlerinizi yönetmek, satış bildirimleri almak ve temel bilgileri görüntülemek için WooCommerce mobil uygulamasını yükleyin.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-21 18:45:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Ürün kataloğunuzu nasıl özelleştirebilirsiniz?', 'Ürün kataloğunuzun ve görsellerinizin harika görünmesini ve markanızla uyumlu olmasını istiyorsunuz. Bu kılavuz, ürünlerinizin mağazanızda harika görünmesi için ihtiyacınız olan tüm ipuçlarını size verecektir.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-27 10:33:58', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Mağazanızda ödemeleri kabul etmeye başlayın!', 'Size uygun sağlayıcı ile ödeme yapın - WooCommerce için 100\'den fazla ödeme ağ geçidi arasından seçim yapın.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-27 10:34:00', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Kullanım izleme özelliğiyle WooCommerce\'in iyileştirilmesine yardımcı olun.', 'Kullanım verilerini toplamak, WooCommerce\'i daha iyi hale getirmemizi sağlar. Yeni özellikler değerlendirilirken, bir güncellemenin kalitesine karar verilirken ya da bir geliştirmenin mantıklı olup olmadığı belirlenirken mağazanız dikkate alınır. Her zaman <a href=\"http://localhost/e-ticaret/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Ayarlar</a> bölümünü ziyaret ederek verilerin paylaşılmasını durdurabilirsiniz. Hangi verileri topladığımıza dair <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">daha fazla bilgi edinin</a>.', '{}', 'unactioned', 'woocommerce-admin', '2024-11-27 10:34:01', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2024-12-09 16:57:39', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'usps-mail-service-to-canada-suspended-dec-2024', 'update', 'en_US', 'Please note: USPS Mail Service to Canada Suspended', 'The United States Postal Service has temporarily suspended mail service to Canada due to the ongoing Canadian postal workers\' strike. USPS is asking users to refrain from mailing items to Canada at this time.', '{}', 'pending', 'woocommerce.com', '2024-12-09 16:57:39', NULL, 0, 'plain', '', 0, 0, 'info');
